
public class Ex04_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100, b = 200;

		System.out.printf(" %d  == %d 는   %s  이다.\n", a, b, a == b);
		System.out.printf(" %d  != %d 는   %s  이다.\n", a, b, a != b);
		System.out.printf(" %d  >  %d 는   %s  이다.\n", a, b, a > b);
		System.out.printf(" %d  <  %d 는   %s  이다.\n", a, b, a < b);
		System.out.printf(" %d  >= %d 는   %s  이다.\n", a, b, a >= b);
		System.out.printf(" %d  <= %d 는   %s  이다.\n", a, b, a <= b);
		System.out.printf(" %d  =  %d 는   %s  이다.\n", a, b, a = b);
	}

}
